mapper.select:
select id,user_name,password,default_Role,user_regist_source,user_type,mobile_phone,`status`,avatar
from t_frame_user
where status in (1,2,3)

    and user_name = #{userName,jdbcType=VARCHAR}


    and id = #{userId,jdbcType=INTEGER}


    and user_Type = #{params.userType,jdbcType=VARCHAR}
